import React, { useMemo } from 'react';
import { Tooltip, Image } from 'antd';
import { InfoCircleOutlined } from '@ant-design/icons';
import classNames from 'classnames';
/**
* valueType类型：
* index 序号
* indexBorder 序号带样式
* picture 图片（缩略图）
* valueTypePictureRender自定义图片
*/
function UseWrapperColumns(columns, pagination) {
    useMemo(() =>
        columns.forEach(val => {
            /**
            * 列title提示
            */
            if (val?.tip && val?.title && !val.lastTitle) {
                val.lastTitle = true; // 初次渲染title后不再渲染title 因为title里面使用title 防止嵌套
                val.title = <>
                    <span style={{ paddingRight: 6 }}>{val?.title}</span>
                    <Tooltip arrowPointAtCenter placement="top" title={val?.tip}>
                        <span style={{ cursor: 'pointer' }}><InfoCircleOutlined /></span>
                    </Tooltip>
                </>;
            }
            /**
                    * 自动生产序号和序号样式
                    */
            if (val.valueType === 'index' || val.valueType === 'indexBorder') {
                val.render = (text, record, index) => {
                    let targetIndex = index + 1;
                    const { current, pageSize } = pagination || {};
                    current && pageSize && (targetIndex = (current - 1) * pageSize + index + 1);
                    const indexClassNames = classNames(
                        'serialNumber',
                        targetIndex > 3 && 'thereBelowSerialNumber'
                    );
                    return <span className="indexBorder">
                        <span className={val.valueType === 'indexBorder' ? indexClassNames : ''}>{targetIndex}</span>
                    </span>;
                };
            }
            /**
             * 缩略图
             */
            if (val?.valueType === 'picture') {
                val.render = (text, record, index) => {
                    const newText = val?.valueTypePictureRender ? val?.valueTypePictureRender(text, record, index) : text;
                    return <Image
                        width={32}
                        src={newText || ''}
                        fallback="data:image/png;base64,"
                    />;
                };
            }
            // 有val.render属性后，所有自定义val.render作废
            if (val.render) return;

            /**
             * 文本省略增加鼠标移入显示全部文本功能
             */
            val?.ellipsis && (val.render = text => <Tooltip arrowPointAtCenter placement="left" title={text}>{text}</Tooltip>);
        }), [pagination, columns]);
    return columns;
}
export default UseWrapperColumns;